parameters.param:
Write-Host "In input wssScanner.forceDownload we have %wssScanner.forceDownload%";
    if ("%wssScanner.forceDownload%".ToLower() -eq "true") {
        $forceDownload = $true;
    } else {
        $forceDownload = $false;
    }

    Import-Module %system.teamcity.build.tempDir%\whitesource-scanner.psm1;

    Scan-Sources -ForceDownload $forceDownload -AgentPath "%wssScanner.scannerTargetPath%" -ProjectName "%wssScanner.projectName%" -WssConfigurationPath "%wssScanner.projectConfig%" -Version "%wssScanner.version%" -FileScanPattern "%wssScanner.scanPattern%" -WssApiKey "%wssScanner.apiKey%" -ScanPath "%wssScanner.scanPath%";
